INSERT INTO `tf_question` VALUES (3, '拥有马克思主义科学理论指导是我们党坚定信仰信念、把握历史主动的根本所在。（ ）', '1', 0, '本题考查时政真题。\r\n\r\n党的二十大报告指出：“拥有马克思主义科学理论指导是我们党坚定信仰信念、把握历史主动的根本所在。”马克思主义是实现中华民族伟大复兴的强大思想武器，是坚定信仰信念、把握历史主动的理论基础。\r\n\r\n故表述正确。', 1.00, 6, 0);
INSERT INTO `tf_question` VALUES (4, '全面建设社会主义现代化国家，最艰巨最繁重的任务仍然在镇街。（ ）', '1', 0, '本题考查时政真题。\r\n\r\n习近平总书记在党的二十大报告中指出：“全面建设社会主义现代化国家，最艰巨最繁重的任务仍然在农村。”这既是对新时代“三农”形势的清醒认识，也是告诫全党，不管工业化、城镇化进展到哪一步，“三农”的基础地位都不会改变，都要始终坚持把解决好“三农”问题作为全党工作重中之重。\r\n\r\n故表述错误。', 1.00, 6, 0);
INSERT INTO `tf_question` VALUES (5, '生态环境是关系党的使命宗旨的重大政治问题，也是关系民生的重大社会问题。（ ）', '1', 1, '本题考查时政真题。\r\n\r\n2018年5月18日至19日，全国生态环境保护大会在北京召开。中共中央总书记、国家主席、中央军委主席习近平出席会议并发表重要讲话。习近平强调，生态环境是关系党的使命宗旨的重大政治问题，也是关系民生的重大社会问题。广大人民群众热切期盼加快提高生态环境质量。我们要积极回应人民群众所想、所盼、所急，大力推进生态文明建设，提供更多优质生态产品，不断满足人民群众日益增长的优美生态环境需要。\r\n\r\n故表述正确。', 1.00, 6, 0);
INSERT INTO `tf_question` VALUES (6, '改革开放是党在新的历史条件下领导人民进行的新的伟大革命，是决定当代中国命运的关键抉择。（ ）', '1', 1, '本题考查中国特色社会主义理论体系。\r\n\r\n改革开放是党在新的历史条件下领导人民进行的新的伟大革命，是决定当代中国命运的关键抉择。改革必须坚持正确方向，既不走封闭僵化的老路，也不走改旗易帜的邪路。我们要把完善和发展中国特色社会主义制度、推进国家治理体系和治理能力现代化作为全面深化改革的总目标。\r\n\r\n故表述正确。', 1.00, 6, 0);
INSERT INTO `tf_question` VALUES (7, '市场在资源配置中起全部作用。（ ）', '1', 0, '本题考查社会主义市场经济体制。\r\n\r\n中共十八届三中全会通过的《中共中央关于全面深化改革若干重大问题的决定》指出，经济体制改革是全面深化改革的重点，核心问题是处理好政府和市场的关系，使市场在资源配置中起决定性作用和更好发挥政府作用。市场决定资源配置是市场经济的一般规律，健全社会主义市场经济体制必须遵循这条规律，着力解决市场体系不完善、政府干预过多和监管不到位问题。\r\n\r\n故表述错误。', 1.00, 6, 0);
INSERT INTO `tf_question` VALUES (8, '全面依法治国是国家治理的一场深刻革命，关系党执政兴国，关系人民幸福安康，关系党和国家长治久安。（ ）', '1', 1, '本题考查时政真题。\r\n\r\n习近平总书记在党的二十大报告中指出：“全面依法治国是国家治理的一场深刻革命，关系党执政兴国，关系人民幸福安康，关系党和国家长治久安。必须更好发挥法治固根本、稳预期、利长远的保障作用，在法治轨道上全面建设社会主义现代化国家。”\r\n\r\n故表述正确。', 1.00, 6, 0);
INSERT INTO `tf_question` VALUES (9, '坚定中国特色社会主义文化自信、道路自信、理论自信，说到底是要坚定制度自信。（ ）', '1', 0, '本题考查中国特色社会主义理论体系。\r\n\r\n坚定中国特色社会主义道路自信、理论自信、制度自信，说到底是要坚持文化自信。文化自信，是更基础、更广泛、更深厚的自信。在5000多年文明发展中孕育的中华优秀传统文化，在党和人民伟大斗争中孕育的革命文化和社会主义先进文化，积淀着中华民族最深层的精神追求，代表着中华民族独特的精神标识。\r\n\r\n故表述错误。', 1.00, 6, 0);
INSERT INTO `tf_question` VALUES (10, '发展是党执政兴国的第一要务，是解决我国一切问题的基础和关键。（ ）', '1', 1, '本题考查时政真题。\r\n\r\n习近平总书记在党的二十大报告中强调，“高质量发展是全面建设社会主义现代化国家的首要任务。发展是党执政兴国的第一要务。没有坚实的物质技术基础，就不可能全面建成社会主义现代化强国。”党的十九大报告指出：“发展是解决我国一切问题的基础和关键，发展必须是科学发展，必须坚定不移贯彻创新、协调、绿色、开放、共享的发展理念。”\r\n\r\n故表述正确。', 1.00, 6, 0);
INSERT INTO `tf_question` VALUES (11, '构建人类命运共同体是世界各国人民前途所在。（ ）', '1', 1, '本题考查时政真题。\r\n\r\n习近平总书记在党的二十大报告中指出：“构建人类命运共同体是世界各国人民前途所在。万物并育而不相害，道并行而不相悖。只有各国行天下之大道，和睦相处、合作共赢，繁荣才能持久，安全才有保障。”\r\n\r\n故表述正确。', 1.00, 6, 0);
INSERT INTO `tf_question` VALUES (12, '\r\n“中华文化认同超越地域乡土、血缘世系、宗教信仰等，把内部差异极大的广土巨族整合成多元一体的中华民族。”这句话说明了中华文明具有突出的连续性。（ ）', '1', 0, '本题考查政治常识。\r\n\r\n中华文化认同超越地域乡土、血缘世系、宗教信仰等，把内部差异极大的广土巨族整合成多元一体的中华民族。体现了中华文明突出的包容性。中华文明突出的包容性涵养了中华民族共同体海纳百川的开放格局。包容性决定了中华民族历史发展的主流是团结和谐。\r\n\r\n故表述错误。', 1.00, 6, 0);
